import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.print("Enter your name: ");        String name = scanner.nextLine();        System.out.print("age:");        int age = scanner.nextInt();        System.out.print("gpa:");        double gpa = scanner.nextDouble();        System.out.print("Are you a student? (true/false): ");        boolean isStudent = scanner.hasNextBoolean();        System.out.println("your age " + age + ".");        System.out.println("your gpa " + gpa + ".");        System.out.println("Hello " + name);        if (isStudent){            System.out.println("You enrolled as a student");        }        else{            System.out.println("Yuou are not enrolled");        }        scanner.close();    }}